pragma solidity ^0.4.23;

// first version from http://140.110.141.7:8888/Chengfeng/server-code/blob/1bcec669015b31e17c122e4e9810a8f46b47c5ac/cert.sol
contract Certificate {

    address owner;
    // sha3("GmyJimmy")
    bytes32 key = 0xe07e453d81f6c0eb7e30faf2d233a3c676723b6fe346a2ab3713aff52a12a5e0;
    bytes32 k = bytes32(address(this)) ^ key;

    event Issue(uint m, address addr);

    // Why Oracle
    // 原始參照設計 Oracle系列二：My Oracle – Taipei Ethereum Meetup – Medium https://medium.com/taipei-ethereum-meetup/oracle%E7%B3%BB%E5%88%97%E4%BA%8C-my-oracle-d86ea6971431
    // 只是只做傳送訊息應是無用確定移除
    // Oracle oracle;

    struct Record {
        bytes32 hash;
        uint8 count;
        address[] addr;
        bool[] verify;
        string swarm;
    }

    mapping( uint => Record ) public record;

    function Certificate() public {
        owner = msg.sender;
    }

    // Why Oracle only for send a event ? 
    //function SetOracle(address _addr) {
    //    oracle = Oracle(_addr);
    //}

    function init(uint _m, bytes20[] _addr, bytes32[] _s) public {
        if(record[_m].hash != 0)   revert();
        for (uint8 i = 0; i < _addr.length; i++) {
            record[_m].addr.push(address(_addr[i]));
            record[_m].verify.push(false);
        }
        for (i = 0; i < _s.length; i++) {
            _s[i] = xor32(_s[i]);
        }
        record[_m].hash = keccak256(_s);
        record[_m].count = 0;
    }


    function verify(uint _m, uint8 _n, uint8 v, bytes32 r, bytes32 s) public {
        if(record[_m].hash == 0)    revert();
        if(record[_m].verify[_n])   revert();

        r = xor32(r);
        s = xor32(s);
        if(ecrecover(record[_m].hash, v, r, s) != xor20(record[_m].addr[_n]))  revert();
        record[_m].verify[_n] = true;
        record[_m].count += 1;
        if(record[_m].count == record[_m].addr.length) {
            // Why Oracle ?
            // oracle.issue(_m);
            Issue(_m, msg.sender);
        }
    }

    function trace(uint _m) public view returns (bool[]) {
        if(record[_m].hash == 0)    revert();

        return record[_m].verify;
    }

    function gethash(uint _m) public view returns (bytes32) {
        if(record[_m].hash == 0)    revert();

        return record[_m].hash;
    }

    // TODO
    // rename to setP2PFileStorage 
    function setswarm(uint _m, string _s) public {
        if(record[_m].hash == 0)    revert();
        if(bytes(record[_m].swarm).length != 0) revert();

        record[_m].swarm = _s;
    }

    function getswarm(uint _m) public view returns (string) {
        if(record[_m].hash == 0)  revert();

        return record[_m].swarm;
    }

    function xor20(address _addr) public view returns(address) {
        return address( ( bytes20(address(this)) ^ bytes20(key) ^ bytes20(_addr) ) );
    }

    function xor32(bytes32 a) public view returns(bytes32) {
        return (a ^ k);
    }

}
